"use strict";

var Hotwire = require('hotwirejs');
var path = require('path');

var internals = {};
internals.options = {};

internals.options.url = process.env.CONFIG_API || 'http://composite.cars.com/config-api/1.0/rest/config';

try {
    internals.options.config = require(path.join(process.cwd(), 'env.json'));
    console.log('WARNING: Using config overrides from env.json');
} catch (e) {
    internals.options.config = {};
}


module.exports = new Hotwire(internals.options);
