"use strict";

var path = require('path');
var config = require('./config');

function FeatureFlags(config) {
    this.options = {
        rootNode : 'featureFlags',
        delimiter : '.'
    };
    this.__config__ = config;
    return this;
}

FeatureFlags.prototype.get = function (key) {
    var k = this.options.rootNode;
    if (key) {
        k = k.concat(this.options.delimiter,key);
    }
    return this.__config__.get(k);
};

FeatureFlags.prototype.isFeature = function (key) {
    return this.get(key);
};

module.exports = new FeatureFlags(config);
