"use strict";

var path = require('path');
var config = require('./config');

function Properties(config) {
    this.options = {
        rootNode : 'properties',
        delimiter : '.'
    };
    this.__config__ = config;
    return this;
};

Properties.prototype.get = function (key) {
    var k = this.options.rootNode;
    if (key) {
        k = k.concat(this.options.delimiter,key);
    }
    return this.__config__.get(k);
};

module.exports = new Properties(config);
